// @User: Advanced
// @Range: 0.25 255
// @Units: s

GRIP_CAN_ID.__field_text:
// @DisplayName: EPM UAVCAN Hardpoint ID
// @Description: Refer to https://docs.zubax.com/opengrab_epm_v3#UAVCAN_interface
// @User: Standard
// @Range: 0 255

GRIP_GRAB.__field_text:
