VISO_SCALE.__field_text:
// @DisplayName: Visual odometry scaling factor
// @Description: Visual odometry scaling factor applied to position estimates from sensor
// @User: Advanced

VISO_TYPE.__field_text:
// @DisplayName: Visual odometry camera connection type
// @Description: Visual odometry camera connection type
// @Values: 0:None,1:MAVLink,2:IntelT265,3:VOXL(ModalAI)
// @User: Advanced
// @RebootRequired: True

VISO_VEL_M_NSE.__field_text:
// @DisplayName: Visual odometry velocity measurement noise
// @Description: Visual odometry velocity measurement noise in m/s
// @Units: m/s
// @Range: 0.05 5.0
// @User: Advanced

VISO_YAW_M_NSE.__field_text:
// @DisplayName: Visual odometry yaw measurement noise
// @Description: Visual odometry yaw measurement noise minimum (radians), This value will be used if the sensor provides a lower noise value (or no noise value)
// @Units: rad
// @Range: 0.05 1.0
// @User: Advanced

VTX_BAND.__field_text:
// @DisplayName: Video Transmitter Band
// @Description: Video Transmitter Band
// @User: Standard
// @Values: 0:Band A,1:Band B,2:Band E,3:Airwave,4:RaceBand,5:Low RaceBand,6:1G3 Band A,7:1G3 Band B

VTX_CHANNEL.__field_text:
// @DisplayName: Video Transmitter Channel
// @Description: Video Transmitter Channel
// @User: Standard
// @Range: 0 7

VTX_ENABLE.__field_text:
// @DisplayName: Is the Video Transmitter enabled or not
// @Description: Toggles the Video Transmitter on and off
// @Values: 0:Disable,1:Enable

VTX_FREQ.__field_text:
// @DisplayName: Video Transmitter Frequency
// @Description: Video Transmitter Frequency. The frequency is derived from the setting of BAND and CHANNEL
// @User: Standard
// @ReadOnly: True
// @Range: 1000 6000

VTX_MAX_POWER.__field_text:
// @DisplayName: Video Transmitter Max Power Level
// @Description: Video Transmitter Maximum Power Level. Different VTXs support different power levels, this prevents the power aux switch from requesting too high a power level. The switch supports 6 power levels and the selected power will be a subdivision between 0 and this setting.
// @Range: 25 1000

VTX_OPTIONS.__field_text:
// @DisplayName: Video Transmitter Options
// @Description: Video Transmitter Options. Pitmode puts the VTX in a low power state. Unlocked enables certain restricted frequencies and power levels. Do not enable the Unlocked option unless you have appropriate permissions in your jurisdiction to transmit at high power levels. One stop-bit may be required for VTXs that erroneously mimic iNav behaviour.
// @User: Advanced
// @Bitmask: 0:Pitmode,1:Pitmode until armed,2:Pitmode when disarmed,3:Unlocked,4:Add leading zero byte to requests,5:Use 1 stop-bit in SmartAudio,6:Ignore CRC in SmartAudio,7:Ignore status updates in CRSF and blindly set VTX options

VTX_POWER.__field_text:
// @DisplayName: Video Transmitter Power Level
// @Description: Video Transmitter Power Level. Different VTXs support different power levels, the power level chosen will be rounded down to the nearest supported power level
// @Range: 1 1000

WINCH_OPTIONS.__field_text:
// @DisplayName: Winch options
// @Description: Winch options
// @Bitmask:  0:Spin freely on startup, 1:Verbose output, 2:Retry if stuck (Daiwa only)
// @User: Standard

WINCH_POS_P.__field_text:
// @DisplayName: Winch control position error P gain
// @Description: Winch control position error P gain
// @Range: 0.01 10.0
// @User: Standard

WINCH_RATE_MAX.__field_text:
// @DisplayName: Winch deploy or retract rate maximum
// @Description: Winch deploy or retract rate maximum.  Set to maximum rate with no load.
// @User: Standard
// @Range: 0 10
// @Units: m/s

WPNAV_ACCEL.__field_text:
// @DisplayName: Waypoint Acceleration 
// @Description: Defines the horizontal acceleration in cm/s/s used during missions
// @Units: cm/s/s
// @Range: 50 500
// @Increment: 10
// @User: Standard

WPNAV_ACCEL_C.__field_text:
// @DisplayName: Waypoint Cornering Acceleration
// @Description: Defines the maximum cornering acceleration in cm/s/s used during missions.  If zero uses 2x accel value.
// @Units: cm/s/s
// @Range: 0 500
// @Increment: 10
// @User: Standard

WPNAV_ACCEL_Z.__field_text:
// @DisplayName: Waypoint Vertical Acceleration
// @Description: Defines the vertical acceleration in cm/s/s used during missions
// @Units: cm/s/s
// @Range: 50 500
// @Increment: 10
// @User: Standard

WPNAV_JERK.__field_text:
// @DisplayName: Waypoint Jerk
// @Description: Defines the horizontal jerk in m/s/s used during missions
// @Units: m/s/s/s
// @Range: 1 20
// @User: Standard

WPNAV_RADIUS.__field_text:
// @DisplayName: Waypoint Radius
// @Description: Defines the distance from a waypoint, that when crossed indicates the wp has been hit.
// @Units: cm
// @Range: 5 1000
// @Increment: 1
// @User: Standard

WPNAV_RFND_USE.__field_text:
// @DisplayName: Waypoint missions use rangefinder for terrain following
// @Description: This controls if waypoint missions use rangefinder for terrain following
// @Values: 0:Disable,1:Enable
// @User: Advanced

WPNAV_SPEED.__field_text:
// @DisplayName: Waypoint Horizontal Speed Target
// @Description: Defines the speed in cm/s which the aircraft will attempt to maintain horizontally during a WP mission
// @Units: cm/s
// @Range: 20 2000
// @Increment: 50
// @User: Standard

WPNAV_SPEED_DN.__field_text:
// @DisplayName: Waypoint Descent Speed Target
// @Description: Defines the speed in cm/s which the aircraft will attempt to maintain while descending during a WP mission
// @Units: cm/s
// @Range: 10 500
// @Increment: 10
// @User: Standard

WPNAV_SPEED_UP.__field_text:
// @DisplayName: Waypoint Climb Speed Target
// @Description: Defines the speed in cm/s which the aircraft will attempt to maintain while climbing during a WP mission
// @Units: cm/s
// @Range: 10 1000
// @Increment: 50
// @User: Standard

WPNAV_TER_MARGIN.__field_text:
// @DisplayName: Waypoint Terrain following altitude margin
// @Description: Waypoint Terrain following altitude margin.  Vehicle will stop if distance from target altitude is larger than this margin (in meters)
// @Units: m
// @Range: 0.1 100
// @User: Advanced

WVANE_ANG_MIN.__field_text:
// @DisplayName: Weathervaning min angle
// @Description: The minimum target roll/pitch angle before active weathervaning will start.  This provides a dead zone that is particularly useful for poorly trimmed quadplanes.
// @Units: deg
// @Range: 0 10
// @Increment: 0.1
// @User: Standard

WVANE_ENABLE.__field_text:
// @DisplayName: Enable
// @Description: Enable weather vaning.  When active, the aircraft will automatically yaw into wind when in a VTOL position controlled mode. Pilot yaw commands override the weathervaning action.
// @Values: -1:Only use during takeoffs or landing see weathervane takeoff and land override parameters,0:Disabled,1:Nose into wind,2:Nose or tail into wind,3:Side into wind,4:tail into wind
// @User: Standard

WVANE_GAIN.__field_text:
// @DisplayName: Weathervaning gain
// @Description: This converts the target roll/pitch angle of the aircraft into the correcting (into wind) yaw rate. e.g. Gain = 2, roll = 30 deg, pitch = 0 deg, yaw rate = 60 deg/s.
// @Range: 0.5 4
// @Increment: 0.1
// @User: Standard

WVANE_HGT_MIN.__field_text:
// @DisplayName: Weathervaning min height
// @Description: Above this height weathervaning is permitted.  If a range finder is fitted or if terrain is enabled, this parameter sets height AGL.  Otherwise, this parameter sets height above home.  Set zero to ignore minimum height requirement to activate weathervaning.
// @Description{Plane}: Above this height weathervaning is permitted.  If RNGFND_LANDING is enabled or terrain is enabled then this parameter sets height AGL. Otherwise this parameter sets height above home.  Set zero to ignore minimum height requirement to activate weathervaning
// @Units: m
// @Range: 0 50
// @Increment: 1
// @User: Standard

WVANE_LAND.__field_text:
// @DisplayName: Landing override
// @Description: Override the weather vaning behaviour when in landing
// @Values: -1:No override,0:Disabled,1:Nose into wind,2:Nose or tail into wind,3:Side into wind,4:tail into wind
// @User: Standard

WVANE_OPTIONS.__field_text:
// @DisplayName: Weathervaning options
// @Description: Options impacting weathervaning behaviour
// @Bitmask: 0:Use pitch when nose or tail-in for faster weathervaning
// @User: Standard

WVANE_SPD_MAX.__field_text:
// @DisplayName: Weathervaning max ground speed
// @Description: Below this ground speed weathervaning is permitted. Set to 0 to ignore this condition when checking if vehicle should weathervane.
// @Units: m/s
// @Range: 0 50
// @Increment: 0.1
// @User: Standard

WVANE_TAKEOFF.__field_text:
// @DisplayName: Takeoff override
// @Description: Override the weather vaning behaviour when in takeoffs
// @Values: -1:No override,0:Disabled,1:Nose into wind,2:Nose or tail into wind,3:Side into wind,4:tail into wind
// @User: Standard

WVANE_VELZ_MAX.__field_text:
// @DisplayName: Weathervaning max vertical speed
// @Description: The maximum climb or descent speed that the vehicle will still attempt to weathervane. Set to 0 to ignore this condition to get the aircraft to weathervane at any climb/descent rate.  This is particularly useful for aircraft with low disc loading that struggle with yaw control in decent.
// @Units: m/s
// @Range: 0 5
// @Increment: 0.1
// @User: Standard

ZIGZ_AUTO_ENABLE.__field_text:
// @DisplayName: ZigZag auto enable/disable
// @Description: Allows you to enable (1) or disable (0) ZigZag auto feature
// @Values: 0:Disabled,1:Enabled
// @User: Advanced

ZIGZ_DIRECTION.__field_text:
// @DisplayName: Sideways direction in ZigZag auto
// @Description: The direction to move sideways in ZigZag mode
// @Values: 0:forward, 1:right, 2:backward, 3:left
// @User: Advanced

ZIGZ_LINE_NUM.__field_text:
// @DisplayName: Total number of lines
// @Description: Total number of lines for ZigZag auto if 1 or more. -1: Infinity, 0: Just moving to sideways
// @Range: -1 32767
// @User: Advanced

ZIGZ_SIDE_DIST.__field_text:
// @DisplayName: Sideways distance in ZigZag auto
// @Description: The distance to move sideways in ZigZag mode
// @Units: m
// @Range: 0.1 100
// @User: Advanced

ZIGZ_SPRAYER.__field_text:
// @DisplayName: Auto sprayer in ZigZag
// @Description: Enable the auto sprayer in ZigZag mode. SPRAY_ENABLE = 1 and SERVOx_FUNCTION = 22(SprayerPump) / 23(SprayerSpinner) also must be set. This makes the sprayer on while moving to destination A or B. The sprayer will stop if the vehicle reaches destination or the flight mode is changed from ZigZag to other.
// @Values: 0:Disabled,1:Enabled
// @User: Advanced

ZIGZ_WP_DELAY.__field_text:
// @DisplayName: The delay for zigzag waypoint
// @Description: Waiting time after reached the destination
// @Units: s
// @Range: 0 127
// @User: Advanced
